import { RelationshipsSchema, AttributesSchema } from "./record-data-schemas";
export interface RecordDataStoreWrapper {
    relationshipsDefinitionFor(modelName: string): RelationshipsSchema;
    attributesDefinitionFor(modelName: string): AttributesSchema;
    setRecordId(modelName: string, id: string, clientId: string): any;
    disconnectRecord(modelName: string, id: string | null, clientId: string): any;
    isRecordInUse(modelName: string, id: string | null, clientId: string): boolean;
    notifyPropertyChange(modelName: string, id: string | null, clientId: string | null, key: string): any;
    notifyHasManyChange(modelName: string, id: string | null, clientId: string | null, key: string): any;
    recordDataFor(modelName: string, id: string, clientId?: string): any;
    notifyBelongsToChange(modelName: string, id: string | null, clientId: string | null, key: string): any;
    inverseForRelationship(modelName: string, key: string): any;
    inverseIsAsyncForRelationship(modelName: string, key: string): any;
}
